#define M_PI 3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 center = iResolution.xy / 2.;
    float r = iResolution.y / 4.;

    fragColor = vec4(0.35, 0.9, 1, 1);

    // round part
    if (distance(fragCoord, center) < r && fragCoord.y > center.y - 1.)
        fragColor = vec4(.9, 0, 0, 1);
    // rectangle part
    float r2 = r / 1.5;
    float r_bottom = center.y - r2;
    if (distance(fragCoord.x, center.x) < r && fragCoord.y > r_bottom && fragCoord.y < center.y + 1.)
        fragColor = vec4(.9, 0, 0, 1);
    // flappy part
    if (distance(fragCoord.x, center.x) < r && fragCoord.y < r_bottom) {
		if (fragCoord.y > (r_bottom - r2)
            + r2 * 0.25 * sin(M_PI / 180. * (float(iFrame * 5) - (fragCoord.x - center.x) * 5.)))
        	fragColor = vec4(.9, 0, 0, 1);
    }

    // eyes
    vec2 eye_pos_1 = center;
    eye_pos_1.y += r / 4.;
    eye_pos_1.x -= r - r / 3. - r / 100.;
    vec2 eye_pos_2 = eye_pos_1;
    eye_pos_2.y -= r / 1.8;
    if (distance(fragCoord, eye_pos_1) + distance(fragCoord, eye_pos_2) <
        distance(eye_pos_1, eye_pos_2) + r / 4.)
        fragColor = vec4(1);
    if (distance(fragCoord, eye_pos_2 - vec2(r/12.,-r/6. + 5. * sin(M_PI / 180. * float(iFrame)))) < r / 6.)
        fragColor = vec4(0, 0, 1, 1);

    eye_pos_1 = center;
    eye_pos_1.y += r / 4.;
    eye_pos_2 = eye_pos_1;
    eye_pos_2.y -= r / 1.8;
    if (distance(fragCoord, eye_pos_1) + distance(fragCoord, eye_pos_2) <
        distance(eye_pos_1, eye_pos_2) + r / 4.)
        fragColor = vec4(1);
    if (distance(fragCoord, eye_pos_2 - vec2(r/12.,-r/6. + 5. * sin(M_PI / 180. * float(iFrame)))) < r / 6.)
        fragColor = vec4(0, 0, 1, 1);
}
