const float N = 9.;
const int NN = 9;

vec3 palette[NN];


vec3 convert(vec3 col){
    float palette_index;
    float c = (col.r+col.g+col.b)/3.;
    palette_index = floor(c*N);

    return palette[int(palette_index)];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // manual palette
    palette[0] = vec3(0.2,0.2,0.2);
    palette[1] = vec3(0.2,0.2,0.8);
    palette[2] = vec3(0.3,0.2,0.7);
    palette[3] = vec3(0.4,0.2,0.6);
    palette[4] = vec3(0.5,0.2,0.5);
    palette[5] = vec3(0.4,0.5,0.4);
    palette[6] = vec3(0.3,0.5,0.3);
    palette[7] = vec3(0.2,0.5,0.2);
    palette[8] = vec3(0.3,0.5,0.1);

    vec2 uv = fragCoord/iResolution.xy;

    vec4 final;
    vec4 tex = texture(iChannel0,uv);
    tex.rgb = convert(tex.rgb);

    final = tex;

    fragColor = final;
}
