void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 convert = vec2(fragCoord - iResolution.xy/2.0);
    //x^2+y^2=r^2
    bool is_black =  dot(convert,convert)<= iResolution.y*100.0;
    vec3 col = is_black ? (0.5-cos((iTime+uv.xyx-vec3(0,2,4)))/2.) : vec3(0.0,0.0,0.0);
    fragColor = vec4(col,1.0);
}
