float sigmoid(float v) {
    return 1./(1.+exp(-v));
}

float sphere(vec2 uv) {
    return length(uv) - 0.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    float d = sphere(uv);

    if (abs(d) < 0.005) {
        fragColor = vec4(0.0,0.,0.0,1.0);   
    } else if (d > 0.) {
        fragColor = vec4(0.8*sigmoid(sin(d*iTime*iTime)*3.) + 0.2,0.2,0.,1.0);   
    } else {
        fragColor = vec4(0., 0.2, 0.8*sigmoid(sin(d*iTime*iTime)*3.) + 0.5,1.0);
    }


}
