#define N 128.0
//#define UNIFORM_SAMPLING
#define USE_TEXTURE



// https://www.shadertoy.com/view/lldBRn + FabriceNeyret2
#define hash2(p) fract(sin( (p) * mat2(127.1,311.7,269.5,183.3) ) *43758.5453 )

float getBluenoiseSample(vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec2 cellId = floor(uv * N * 0.5); // Bottom of left of the image split into N
                                       // cells across both X and Y

    #ifdef UNIFORM_SAMPLING
    vec2 jitter = vec2(0.5);
    #else
    vec2 jitter = ((hash2(cellId)) - 0.5) / sqrt(2.0) + 0.5;
    #endif

    /*
    vec2 nearestPixel = (cellId + jitter) / (N * 0.5); // localise jitter to cell
         nearestPixel *= iResolution.y;                // multiply by square dim
         nearestPixel += iResolution.xy;               // match to the actual image sdim
         nearestPixel *= 0.5;
         nearestPixel = floor(nearestPixel) + 0.5;     // make sure target pixel is the center
    */
    // Simplified of the above
    vec2 nearestPixel = floor(
         iResolution.y * (cellId + jitter) / N
         + iResolution.xy * 0.5
    ) + 0.5;

    return float(
        all(bvec4(
            equal(fragCoord.xy, nearestPixel.xy),
            lessThanEqual(abs(uv), vec2(1.))
         ))
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float mask = getBluenoiseSample(fragCoord);

    #ifdef USE_TEXTURE
    fragColor = vec4(texture(iChannel0, fragCoord.xy / iResolution.xy) * mask);

    #else
    fragColor = vec4(mask);

    #endif
}
