float plot(vec2 st, float blur) {    
    return smoothstep(0.376, 0.028+blur, abs(st.y));
}

float plotNeon(vec2 st) {
    return smoothstep(0.832, 1.148, 1.0-abs(st.y));
}

float plotTop(float xd) {
    return smoothstep(1.008, 0.004, xd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 background = plotTop(clamp(st.y, 1.0, 0.376)) * vec3(0.980,0.931,0.974);

    float bottomLine = plot(st, st.y*sin(iTime*10.0)*0.340);
   	float plotNeon = plotNeon(st);
    vec3 lineColor = background * vec3(0.980,0.336,0.946);

    lineColor = bottomLine*lineColor+plotNeon;


	fragColor = vec4(lineColor,1.0);
}
