#define pi 3.14159265358979

//from https://www.shadertoy.com/view/MsS3Wc thanks iq! \(`v`)/
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

float invLerp(float val, float small, float big){
    return ( abs(val-small) )/(big-small);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv = vec2(  1.-invLerp(mod(atan(uv.y,uv.x)+pi*.5, 2.*pi),0.,2.*pi), length(uv));

    float n = 7.;
    vec2 ax = vec2(1.,1.);
    uv -= iTime*.75;

    uv *= mat2(normalize(ax),normalize(vec2(ax.x,-ax.y)));
    uv = floor(uv*n*length(ax))/n;
    float base = sin((uv.x-uv.y));
    fragColor = vec4(hsv2rgb(vec3(base,.6,1.0)),1.0);
}
