 const float PI=acos(-1.0);
 vec2 rot(vec2 p,float a){return vec2(p.x*cos(a)-p.y*sin(a),p.x*sin(a)+p.y*cos(a));}

 vec3 urokomonyou(vec2 p,float scale,vec3 color,vec3 color2)
 {
     vec2 q=scale*p;
     q=rot(q,.00001);
     vec2 inds = 2.*floor(mod(q,2.))-1.;
     q.x*=inds.x*inds.y;
     float deg=q.y-q.x;

     float index=floor(mod(deg,2.));
     return (1.-index)*color + index*(color2);
 }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord* 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 ichigoiro=vec3(187.,85.,97.)/255.;
    vec3 shinku=vec3(173.,.0,45.)/255.;
    vec3 col=urokomonyou(p, 7.,ichigoiro,shinku);
    fragColor = vec4(col,1.0);
}
