//IMG
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.y;
    uv.x-=(iResolution.x-iResolution.y)/iResolution.y / 2.;
    fragColor = vec4(smoothstep(.3,.7,texture(iChannel1, uv).r));
    fragColor = vec4(texture(iChannel1, uv).brga);
}

//A
#define TIMESTEP 1.

#define GRID (step(.45,length(mod(uv,1./RC)*RC-.5)))
#define F (0.12  + .01 * (iMouse.y/iResolution.y-.5))
#define K (0.054  + .01 * (iMouse.x/iResolution.x-.5))
#define Da (1.)
#define Db (.3*smoothstep(0.,5.,iTime))
#define RADIUS 8.

vec2 p2d(vec2 polar) {
    float alpha = polar.x;
    float R = polar.y;
    float x = sin(alpha) * R;
    float y = cos(alpha) * R;
    return vec2(x, y);
}

vec2 d2p(vec2 decart) {
    float alpha = atan(decart.x, decart.y);
    float R = length(decart);
    return vec2(alpha, R);
}

vec2 snowflakeSymmetry(vec2 p, float rays){
	vec2 ar = d2p(p);
	float astep = 2. * 3.1415 / rays;
	float a = ar.x, r = ar.y;
	a = mod(a, astep);
	a -= astep / 2.;
	a = abs(a);
	p = p2d(vec2(a, r));	
	return p;
}

float rnd(float x) {return fract(54321.987 * sin(987.12345 * x));}

void getVal(vec2 p, out vec2 val, out float diff_a, out float diff_b) {
  vec2 r = iResolution.xy;
  vec2 uv = p / r;
  uv -= .5;
  uv = snowflakeSymmetry(uv,3.);
  if(length(uv)>.48) {
      diff_a=diff_b=0.;
      return;
  }
  uv += .5;
  //p = uv*r;

  val = texture(iChannel0, uv).xy;
  vec2 neigh_val_a = vec2(0.);
  vec2 neigh_val_b = vec2(0.);
  vec2 k_anisotropy = abs(normalize(uv-vec2(.5)));
  if(uv.x<.5)k_anisotropy = k_anisotropy.yx;
  float k_sum = 0.;
  for(float i = -RADIUS; i <= RADIUS; i++){
      for(float j = -RADIUS; j <= RADIUS; j++){
          if(i==0. || j==0.) continue;
          if(length(vec2(i,j))>.8*RADIUS) continue;
          vec2 k = vec2(1.);
          k_sum += k.x+k.y;
          vec2 pij = p+vec2(i,j);
          neigh_val_a += texture(iChannel0, (pij) / r).x * k;
          neigh_val_b += texture(iChannel0, (pij) / r).y * k;
      }
  }
  neigh_val_a /= k_sum;
  neigh_val_b /= k_sum;
  diff_a = (-val.x+neigh_val_a.x+neigh_val_a.y)/2.;
  diff_b = (-val.y+neigh_val_b.x+neigh_val_b.y)/2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy) / iResolution.y;

    vec3 colour = vec3(0.0);
    if (iFrame < 1) {
        colour = vec3(rnd(uv.x+uv.y*.0001));
    } else {
     	vec2 val;
        float diff_a, diff_b;
        getVal(fragCoord.xy, val, diff_a, diff_b);

        vec2 delta;
 		delta.x = Da * diff_a - val.x * val.y * val.y + F * (1.0 - val.x);
  		delta.y = Db * diff_b + val.x * val.y * val.y - (K + F) * val.y;

        colour = vec3(val + delta * TIMESTEP, 0.0);
        colour = clamp(colour, -1., 1.);
    }

    fragColor = vec4(colour,1.0);
}

//B
#define res iResolution.xy

mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}

float dist(vec3 p){
	p.y += .2*texture(iChannel0, (p.xz)*vec2(.25,.25)+vec2(.5)).r;
    float sp = length(p+vec3(0.,5.,0.))-2.;
    return sp;
}

vec3 norm(vec3 p){
    vec2 e = vec2(.01,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxy)-dist(p-e.yxy),
        dist(p+e.yyx)-dist(p-e.yyx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 FC )
{
    float d=0., e, i;
    vec3 p,rd=normalize(vec3((FC*2.-res)/res.xy,2.));
    rd.yz*=rot(3.1415/2.);
    for(i=0.;i++<99.;){
        p = d*rd;
        d+=e=dist(p);
        if(e<.01)break;
    }
    //p.xz*=rot(iTime*.1);
    fragColor = 4.*vec4(vec3(dot(norm(p),vec3(1.)))*.2+.6,1.);
    fragColor += texture(iChannel1, norm(p));
    fragColor /= 5.;
    fragColor /= i/3.;
}
