void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 size = iResolution;
    float scaledTime = iTime * 0.1;
    float timePulse;
    if(mod(scaledTime, 2.0) > 1.0)
    {
    	timePulse = mod(iTime, 1.0);
    } else {
        timePulse = mod(-iTime, 1.0);
    }

    vec2 texCoord = vec2(mod(iTime / 50.0, 64.0), mod(iTime / 50.0 / 64.0, 6.0));

    float noise0 = (texture(iChannel0, texCoord).r + texture(iChannel0, texCoord).r + texture(iChannel0, texCoord).r) / 3.0;
    float noise2 = (texture(iChannel2, texCoord).r + texture(iChannel2, texCoord).r + texture(iChannel2, texCoord).r) / 3.0;;
    vec4 starTex = texture(iChannel1, fragCoord / size.xy);

    vec4 screenPosition;
    screenPosition.r = sin(((fragCoord.x / size.y) + (fragCoord.y / size.y)) / 2.0 * 3.14);
    screenPosition.g = sin(((fragCoord.x / size.y / 2.5) + (fragCoord.y / size.y)) / 2.0 * 10.0);
    screenPosition.b = (sin((fragCoord.x * 3.14 * 2.0 / size.x) + (noise0 * 3.14 * 2.0)) + 1.0) / 2.0;

    screenPosition.a = 1.0;
    fragColor = screenPosition;

    if(starTex.r > noise2)
    {
        fragColor = starTex;
    }
}
