// draw scene with SDF shadows

void mainImage (out float4 fragColor, in float2 fragCoord)
{
	fragColor = float4(0.0);
	fragColor.b = texture( iChannel0, fragCoord/iResolution.xy ).r;

	float2		light_pos	= SinCos( iTime * 0.25 ) * 0.35 + 0.5;
	float2		img_scale	= 1.0 / float2(textureSize( iChannel1, 0 ).xy);
    float		scale		= Min( img_scale.x, img_scale.y );
	const int	max_iter	= MAX_ITERATIONS;
	const float	min_dist	= 0.00625;
	const float	max_dist	= float(RADIUS);
	float2		origin		= fragCoord / iResolution.xy;
	float		t			= 0.0;
	float2		pos			= origin;
	float2		dir			= Normalize( light_pos - pos );
	float2		abs_dir		= Abs( dir );
	float		tmax		= Max( Length( light_pos - pos ) - 0.0001, 0.001 );
	bool2		bdir		= Greater( abs_dir, vec2(0.0001) );

	int i = 0;
	for (; i < max_iter; ++i)
	{
#	if ENABLE_PER_AXIS_SDF
		float2	dd = texture( iChannel1, pos ).rg;
		dd.x = (bdir.x ? dd.x / abs_dir.x : max_dist);
		dd.y = (bdir.y ? dd.y / abs_dir.y : max_dist);
		float	d = Min( dd.x, dd.y ) * 0.75;
#	else

		float	d = texture( iChannel1, pos ).r;
#	endif

		if ( d < min_dist )
			break;

		pos += dir * d * scale;
		t = Length( pos - origin );

		if ( t > tmax )
			break;
	}

#if VIEW_HEATMAP
	fragColor = float4(float(i) / float(max_iter));
#else
	if ( t > tmax )
		fragColor.r = 0.2 / tmax;
#endif
}

// generate 2D scene

// DHash from https://www.shadertoy.com/view/4djSRW 
// range [0..1]

float4 DHashScale_float ()  { return float4( 0.1031, 0.1030, 0.0973, 0.1099 ); }

float4 DHash42 (const float2 p)
{
	float4 p4 = Fract( float4(p.xyxy) * DHashScale_float() );
	p4 += Dot( p4, p4.wzxy + 19.19 );
	return Fract( (p4.xxyz + p4.yzzw) * p4.zywx );
}


float SDF_Box (const float3 position, const float3 halfSize)
{
	float3  d = Abs( position ) - halfSize;
	return Min( Max( d.x, Max( d.y, d.z ) ), 0.0 ) + Length( Max( d, 0.0 ) );
}

float SDF (const float2 coord)
{
	float2	ipart	= Floor( coord );
	float2	fpart	= Fract( coord );
	float	md		= 2147483647.0;
	float	seed	= 2.76543;

	for (int y = -2; y <= 2; ++y)
	for (int x = -2; x <= 2; ++x)
	{
		float2	cur		= float2(x,y);
		float4	h		= DHash42( ipart + cur + seed );
		float2	hsize	= Lerp( float2(0.2), float2(1.0), h.xy );
		float2	pos		= h.zw + cur - fpart;
		float	d		= SDF_Box( float3(pos, 0.0), float3(hsize, 1.0) );

		md = Min( md, d );
	}
	return md;
}

void mainImage (out float4 fragColor, in float2 fragCoord)
{
	if ( iFrame > 1 )
		discard;

	float	scale	= 8.0 / min(iResolution.x, iResolution.y);
	float2	uv		= fragCoord * scale;
	float	d		= SDF( uv );

	fragColor = float4( d < 0.0 ? 0.0 : 1.0 );
}

// calculate SDF for scene

void mainImage (out float4 fragColor, in float2 fragCoord)
{
	if ( iFrame > 1 )
		discard;

	int2	coord		= int2(fragCoord);
	float2	dist		= float2(float(RADIUS));
	bool	is_outer	= texelFetch( iChannel0, coord, 0 ).r < 0.25;

	for (int y = -RADIUS; y <= RADIUS; ++y)
	for (int x = -RADIUS; x <= RADIUS; ++x)
	{
		int2	pos			= coord + int2(x,y);
		bool	is_outer2	= texelFetch( iChannel0, pos, 0 ).r < 0.25;
		float	d			= Distance( float2(coord), float2(pos) );

		if ( is_outer != is_outer2 )
		{
#		if ENABLE_PER_AXIS_SDF
			float2	norm	= Abs(Normalize( float2(pos) - float2(coord) ));
			bool2	bnorm	= Greater( norm, float2(0.0001) );

			if ( bnorm.x )	dist.x = Min( dist.x, d / norm.x );
			if ( bnorm.y )	dist.y = Min( dist.y, d / norm.y );
#		else
			dist = float2(Min( dist.x, d ));
#		endif
		}
	}

	dist -= 0.5;

	if ( !is_outer )
		dist = -dist;

	fragColor = float4(dist.rgrg);
}
