vec2 norm(vec2 inp) {
    vec2 r = 2.0 * inp / iResolution.xy - 1.0;
    r.y *= -1.0;
    return r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates to (-1 ... 1)
    vec2 uv = norm(fragCoord);

    float s = 1.0/2.0 * (uv.y/2.0 + 0.5);
    if (s > 0.0) {
        float theta = mod(iTime, radians(360.0));
        mat2 tf = mat2(
            cos(theta), -sin(theta),// Col 1
            sin(theta),  cos(theta) // Col 2
        )/s;
        vec2 coord = tf * uv;
        vec2 mouse = tf * norm(iMouse.xy) * -1.0;
        fragColor = texture(iChannel0, coord)/(sqrt(pow(coord.x + mouse.x, 2.0) + pow(coord.y + mouse.y, 2.0))+1.0);
    }
}
