mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.y*2.0*1.0;
    uv = uv*rot(sqrt(length(uv))*3.1415926535*5.5);

    uv = uv*rot(-iTime);

    vec3 col = vec3(pow(sin(uv.y*3.14159265*1.0)/2.0+0.5, 2.0));

    fragColor = vec4(col, 1.0);
}
