# define PI 3.141592653589793
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    vec2 u = fract(uv.xy*25.);
    vec2 s = vec2(1.0,1.0);

    u-=0.5;
    vec2 g = iTime*0.2+floor(uv*5.);

    float r = 1.0;
    float angle = PI*0.5+floor(rand(floor(g))*4.)*PI*2.5;      
    u.x+=sin(angle)*r;
    u.y+=cos(angle)*r;

    float d = length(u);
 	//float k = smoothstep(d,d*1.01,0.5);
    float k =fract(d*1.0-iTime*0.7);

    k -= step(length(s),0.93);
    fragColor = vec4(k);
}
