#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col =  0.7*cos(iTime*0.3+uv.xyy+vec3(0,2,11));

    uv.y = fract(uv.x+0.01+uv.y-0.2);

    //uv = fract(uv*1.98)-0.01;
    vec2 u = uv;


    if(uv.y>0.37){
        uv.x=fract(uv.x-0.9)*1.0;
    }

    if(uv.x<0.12345){
        col = vec3(0.0,0.0,uv.y+fract(iTime*0.01));
    }
    if(uv.x < 0.3){
        uv.x+=rand(u)*0.15;
    }

    if(uv.x>0.75){
        col = vec3(1.0,0.0,uv.y);
    }
    if(uv.y>0.123456){
        col.r += u.y;
    }

    if(u.x<0.03){
        col = vec3(1.);
    }

    if(u.x>0.28){
        col.g *= rand(uv)*0.1;
    }

    if(u.y>0.28 && mod(fragColor.y,3.0)<0.5){
        col.g += 0.5;
    }

    if(u.y>0.9){
        col.b *= sin(iTime*.1);
    }
         //  u = rotate2d( sin(iTime*0.2)*PI ) * uv;

    if(u.x>0.9){
        col.rb-= uv.y*0.33;
    }

    if(u.x>0.99 && u.y < 0.9){
        col.rgb /= mod(fragCoord.x,2.0); 
    }

    vec2 e = fract(iTime*0.1+abs(uv-0.2)*2.0);

    if(e.x>u.y){
        col.rgb  = 0.7 + col.brg;
    }

    fragColor = vec4(col,1.0);
}
