void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv =  ( fragCoord - .5* iResolution.xy ) / iResolution.y; 

  float t = iTime * 7.;
  t+=sin(t*.5)*2.0+1.6;
  uv.y += sin(t*0.5+uv.x*7.0)*0.1;

  float d = .2;
  uv.y -= d;
  float s = ceil(uv.x);
  uv.y += s * d * 2.;

  t *= s * 2. - 1.;
  //t+= s*3.2;

  float k = sin(t + length(uv) * 55.);
  fragColor = vec4( k/fwidth(k) );
}
