float mand(vec2 z, vec2 c, float steps) {
    float iter = 0.0;
    float sqrDist = 0.0;
    for (;iter<steps;iter+=1.0) {
        float rr = z.x*z.x;
        float ii = z.y*z.y;
        float tmpR = rr - ii + c.x;
        float tmpI = z.x * z.y + z.y * z.x + c.y;
        sqrDist = rr*rr + ii*ii;
        if (sqrDist > 4.0) {
            break;
        }
        z.x = tmpR;
        z.y = tmpI;
    }
	return float(iter) - (log (log (sqrt(z.x*z.x+z.y*z.y)))) / log(2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 mandelCoords = uv.xy*4.0-2.0;
    float cx = sin(iTime*0.5)*300.0/ iResolution.x;
    float cy = cos(iTime*0.5)*300.0/ iResolution.y;
    vec2 c = vec2(cx,cy);
   	//float steps = sin(iTime)*40.0+45.0;
    float steps = 60.0;
    float mu = mand(mandelCoords, c, steps);
    vec3 col = vec3( 1.0 - mu / float(steps));
    fragColor = vec4(0.5 + 0.5*cos(col + vec3(0.0,.8,sin(iTime)*0.5+0.5)),1.0);
}
