float drawCircle(vec2 uvN, float radius, float width)
{
	float a = distance(vec2(0.),uvN);
    a = step(a,radius) - step(a, radius - width);
    return a;
}

float distort(float base, vec2 uvN)
{
    base *= sin(uvN.x/iResolution.x*sin(iTime/20.)*1000.);
    return base;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy/2.)/iResolution.x*800.;
    float cSize = distort(200., uv);
	vec3 col = vec3(drawCircle(uv, cSize, 125.))*fragCoord.x/iResolution.x;

    fragColor = vec4(col,1.0);
}
