float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float mDist(vec2 p,vec2 o){
	return abs(p.x - o.x) + abs(p.y - o.y);
}
float dist(vec2 p,vec2 o){
    return length(p - o);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float min1 = 100000.0;
    float min2 = 100000.0;
    for(float xi= -0.3; xi < 1.0; xi+= 0.2){
        for(float yi = -0.3; yi < 1.0; yi += 0.2){
            float x = xi + sin(iTime * rand(vec2(xi,yi))) * 0.1;
            float y = yi + cos(iTime * rand(vec2(yi,xi))) * 0.1;
        	float a1 = dist(uv,vec2(x,y));
            float a2 = mDist(uv,vec2(x,y));
            float a = mix(a1,a2,smoothstep(0.0,1.0, sin(iTime * 2.)/2. + 1./2.) );
            min2 = min1 > a ? min1 : min2;
            min1 = min1 > a ? a : min1;
        }
    }
    vec3 baseCol = mix(vec3(1.,0.,0.),vec3(0.,1.,1.),
                      smoothstep(0.0,1.0, sin(iTime * 2.)/2. + 1./2.)
                      );
    vec3 col = min2 - min1 < 0.01 ? baseCol :
    	(min2 - min1) * 5.0 * baseCol;

    fragColor = vec4(col,1.0);
}
