const float PI = 3.14159265;


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

float time = iTime *0.2;

    float color1, color2, color;

	color1 = (floor(dot(fragCoord.xy,vec2(cos(time*3.0),sin(time*3.0)))*0.02+time*1.0)+1.0)/2.0;

	vec2 center = vec2(640.0/2.0, 360.0/2.0) + vec2(640.0/2.0*-cos(-time*3.0),360.0/1.0*cos(-time*3.0));

	color2 = (ceil(length(fragCoord.xy - center)*0.03)+1.0)/2.0;

	color = (color1+ color2)/2.0;

	float red	= (sin(PI*color/0.5+time*3.0)+1.0)/2.0;
	float green	= (tan(PI*color/0.5+time*3.0)+1.0)/2.0;
	float blue	= (sin(+time*3.0)+1.0)/2.0;

    fragColor = vec4(red, green, blue, 3.0);
}
