float generateCircle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    return smoothstep(r, r-blur, d);

}

vec2 repositionOriginToCenter(vec2 fragCoord) {
    vec2 uv = fragCoord.xy /iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
	uv -= vec2(1, 0.5);
    return uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = repositionOriginToCenter(fragCoord);

    vec3 originalMask = vec3(generateCircle(uv, vec2(-0.5 * sin(iTime), 0.5 * cos(iTime)), 0.3, 0.5));
    vec3 mask2 = vec3(generateCircle(uv, vec2(-0.2 * sin(iTime), -0.6 * cos(iTime)), 0.3, 0.4));
    vec3 mask3 = vec3(generateCircle(uv, vec2(-0.2 * -0.5*cos(iTime), -0.0 * sin(iTime * 2.0)), 0.3, 0.4));
    vec3 mask4 = vec3(generateCircle(uv, vec2(-0.2 * sin(iTime), -0.5 * sin(iTime * 2.0)), 0.5 * cos(iTime), 0.4));

    vec3 colorBlend = vec3(0.0, 0.0, 1.0) * originalMask;
    colorBlend += vec3(1.0, 0.0, -1.0) * mask2;
    colorBlend += vec3(0.0, 1.0, 0.0) * mask3;
    colorBlend += vec3(1.0, 1.0, 0.0) * mask4;


    fragColor = vec4(colorBlend, 1.0);
}
