#define S smoothstep

float N21(vec2 p){
	return fract(sin(p.x*123.230+p.y*2345.36)*256.753);
}

vec3 t(float d,float k1, float k2, float k3){
    return vec3(S(.005,0.,d-.015)*(sin(iTime+k1)*.5+.5),S(.005,0.,d-.015)*(sin(iTime+k2)*.5+.5),S(.005,0.,d-.015)*(sin(iTime+k3)*.5+.5));;
}


vec3 dick(vec2 uv, vec2 p){
	vec3 col = vec3(0);
    float d = length(uv-p+vec2(-.015, +.05)-.1);
    col += t(d, N21(p+vec2(12.,312.))*500.,N21(p+vec2(19.,372.))*500.,N21(p+vec2(53.,362.))*500.);

    d = length(uv-p+vec2(.015, +.05)-.1);
    col += t(d, N21(p+vec2(162.,312.))*500.,N21(p+vec2(12.,3122.))*500.,N21(p+vec2(22.,82.))*500.);

    vec2 uv1 = uv - p - .1;
    d = length(uv1-vec2(0, clamp(uv1.y,-0.027,.07)));
    col += t(d, N21(p+vec2(42.,352.))*500.,N21(p+vec2(126.,132.))*500.,N21(p+vec2(12.,352.))*500.);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv += iTime*.2;


    vec2 gv = floor(uv*3.)/3.;

    float angle = iTime * (N21(gv)*2.-1.) + N21(gv);
    uv -= gv + vec2(0.1);
    uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    //vec2 rf = uv;
    uv += gv + vec2(0.1);

    vec3 col;

    col = dick(uv,gv); //+N21(gv)*.3
    //col.rg = gv-iTime*.2+1.;//fract(uv*2.)/2.;

    fragColor = vec4(col,1.0);
}
