float noise2d(in vec2 p){

    return fract(sin(p.x*100.+p.y*6574.)*5647.);

}
float smoothnoise(vec2 uv,float tile){
vec2 local_uv = smoothstep(0.,1.,fract(uv*tile));
    //vec2 local_uv = uv;
    vec2 grid_id = floor(uv*tile);

    float bl = noise2d(grid_id);
    float br = noise2d(grid_id+vec2(1.,0.));

    float tr = noise2d(grid_id+vec2(1.));
    float tl = noise2d(grid_id+vec2(0.,1.));

    float b = mix(bl,br,local_uv.x);

    float t = mix(tl,tr,local_uv.x);

    float c = mix(b,t,local_uv.y);
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv.x*=iResolution.x/iResolution.y;
    uv-=vec2(cos(iTime*.01)+sin(iTime*.1));
    float c = smoothnoise(uv,4.);
    c += smoothnoise(uv,8.)*.5;
    c += smoothnoise(uv,16.)*.25;
    c += smoothnoise(uv,32.)*.125;
    c += smoothnoise(uv,64.)*.125/2.;
    c += smoothnoise(uv,128.)*.125/4.;

    vec3 col = vec3(c);
    col.b *= 1.2;
    //col.rg = grid_id*.1;

    // Output to screen
    fragColor = vec4(col,1.0);
}
