// golfing "Xor Stack" by yx: https://shadertoy.com/view/3llXz2 (528 chars)

void mainImage(out vec4 O, vec2 u) {
    for ( float t = fract(iTime/4.);
          O.a++ < 12.; t += .1/60.) {
        vec2 R = iResolution.xy, s = vec2(-3,3),
             U = 15.* (u+u-R)/R.y / exp2(t);
        vec4 p = .33 +smoothstep(.2,0.,t-.1*vec4(1,3,5,7) );
        p = max( abs(U.x-p*s.xyyx), abs(U.y-p*s.yyxx) ) -2.;
        p.x*p.y*p.z*p.w < 0. ? O += .1 : O;
    };
