vec2 uv;
vec3 rainbow1(float h )
{
	h = fract(h);
	return vec3(smoothstep(0.9, 1.0, h) + smoothstep(0.5, 0.4, h), 
		    smoothstep(0.3, 0.2, h) * smoothstep(0.6, 0.5, h),
		    smoothstep(0.1, 0.2, h) * smoothstep(7.0, 0.8, h));
}

float pxl=200000.;

vec2 cln(vec2 v){	
	return vec2(log(length(uv) - 0.001), atan(uv.y, uv.x)) / 6.283 +pxl;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(mod(iTime,4.)>2.)pxl=0.;
    uv =  (fragCoord - 0.5*iResolution.xy)/iResolution.y;
	uv = cln(uv);

	vec3 bg_color = mix(
		rainbow1(uv.x*7.0+(uv.y-0.25*iTime*4.)),
		vec3(0),
		.6);

	vec3 fg_color = mix(
		rainbow1(uv.x+(uv.y*3.0)),
		vec3(0),
		0.1);

	fragColor = vec4(fg_color-bg_color * 1.5, 1);
}
