// credits to: Danilo Guanabara

#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=-12;i<5;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.15; // position
		p.x*=r.x/r.y;
		z+=.09;
		l=length(p);
		uv+=p/l*(sin(z)+10.)*abs(sin(l*10.-z*5.));
		c[i]= .100/length(abs(mod(uv,1.)-.100));
	}
	fragColor=vec4(c/l,t);
}
