vec2 ClosestPointOnLine(vec2 a, vec2 b, vec2 p)
{
    vec2 v = b - a;
    return a + v * clamp(dot(v, (p - a) / dot(v,v)), 0.0, 1.0);
}

vec2 NormalizeSpace(vec2 s)
{
    return (s.xy - 0.5 * iResolution.xy) / iResolution.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float smoothness = 2.0/iResolution.y;
    float thickness = 1.0;

    vec2 uv = NormalizeSpace(fragCoord) * 4.0;
    vec2 muv = NormalizeSpace(iMouse.xy) * 4.0;

    vec2 start = vec2(muv);
    vec2 end = vec2(2.0, 0.0);
    float col = length(uv-ClosestPointOnLine(start, end, uv));
    col = smoothstep(thickness, thickness + smoothness, col);

    // Output to screen
    fragColor = vec4(col,col, col,1.0);
}
