// A mix of two shaders by jarble:
// Automaton: https://www.shadertoy.com/view/WljcRG
// Background pattern: https://www.shadertoy.com/view/ttjXDK

vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 magnify(vec2 fragCoord,float mag){
	return hash32(floor(fragCoord/pow(2.0,mag)));
}

//modified from https://www.shadertoy.com/view/wtlcR8
void mainImage(out vec4 O, vec2 U) {
  int x = int(U),
      y = int(U.y + 30. * iTime),
      r = (x)^(y);
   O = vec4( abs(r*r*r)/(y+int(iTime*50.)) % 9970 < 1000 );

   vec3 color1 = 0.*vec3(.1,.1,.1);
    float maximum = 1.0+5.*(.5*sin(iTime/10.0)+.5);
    for(float i = 0.0; i < maximum; i+=0.618){
    	color1 += magnify(U,i+2.0);
    }
    O = O-vec4(color1/(maximum+0.),0.1);
}
