float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

const float maxVal = 0.5;
const float minVal = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 textureUv = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.y;

    vec4 col = texture(iChannel0, textureUv);

    vec2 squareUV = fract(uv * 100.0);
    float tVal = sin(iTime+uv.x*2.0);
    float lng = circle(squareUV, maxVal- tVal * minVal);

    if(lng <= 0.1)
        col = vec4(0.0);

    fragColor = col;
}
