//Constants
const float tau = atan(1.0)*8.0;
const float pi = atan(1.0)*4.0;




float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdRhombus( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p);
    float h = clamp((-2.0*ndot(q,b)+ndot(b,b))/dot(b,b),-1.0,1.0);
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}


vec3 pierna(vec2 uv, vec3 col, float ptime)
{
     float es=2.0;
    float radio1=0.8*es;
    float radio2=0.45*es;
    float radioPata1=0.15*es;

    vec2 pa0=vec2(0.0,0.0 );
    float tt=ptime;
    float t1=sin(ptime);

    vec2 pa1=pa0+ radio1*0.5*cossin(0.0);                         ///cuerpo
    vec2 pa2=pa1+ radio2*0.5*cossin(radians(270.0)+0.75*sin(t1)); //pierna A
    vec2 pa3=pa2+ radio2*0.5*cossin(radians(250.0)+0.75*sin(t1+2.0*sin(t1))); //rodilla B
    vec2 pa4=pa3+ radioPata1*0.5*cossin(clamp(t1, -0.5,0.0) ); //pata C


    vec2 pb0=vec2(0.0,0.0 );
    vec2 pb1=pb0+ radio1*0.5*cossin(6.0+2.0*sin(ptime));
    vec2 pb2=pb1+ radio2*0.5*cossin(8.0+0.5*cos(ptime));




    float lPiernaAFrente=dfLine(pa1, pa2, 0.05,  uv-vec2(-0.5,0.0));
    float lPiernaBFrente=dfLine(pa2, pa3, 0.05,  uv-vec2(-0.5,0.0));
    float lPiernaCFrente=dfLine(pa3, pa4, 0.05,  uv-vec2(-0.5,0.0));


    lPiernaAFrente=opS(lPiernaBFrente,lPiernaAFrente);
    lPiernaBFrente=opS(lPiernaCFrente,lPiernaBFrente);


    lPiernaAFrente=Getfwidth(lPiernaAFrente);
    lPiernaBFrente=Getfwidth(lPiernaBFrente);
    lPiernaCFrente=Getfwidth(lPiernaCFrente);


    col=mix(vec3(0.0,0.0,0.0) ,col, lPiernaAFrente);
    col=mix(vec3(0.0,0.0,0.0) ,col, lPiernaBFrente);
    col=mix(vec3(0.0,0.0,0.0) ,col, lPiernaCFrente);

    return col;
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 2.0;
    vec3 col = vec3(1.0);


    float es=2.0;
    float radio1=0.8*es;
    float radio2=0.45*es;
    float radioPata1=0.15*es;
    float t1=sin(iTime);
    float tt=iTime;

    vec2 pa0=vec2(0.0,0.0 );
    vec2 pa1=pa0+ radio1*0.5*cossin(0.0);                         ///cuerpo
    vec2 pa2=pa1+ radio2*0.5*cossin(radians(270.0)+0.75*sin(t1)); //pierna A
    vec2 pa3=pa2+ radio2*0.5*cossin(radians(250.0)+0.75*sin(t1+2.0*sin(t1))); //rodilla B
    vec2 pa4=pa3+ radioPata1*0.5*cossin(clamp(t1, -0.5,0.0) ); //pata C

    //cabeza y cuello
    float  radio5=0.6*es;;
    vec2 pa5a=vec2(0.3,0.0);
    vec2 pa5=pa5a+radio5*0.5*cossin(radians(45.0+10.0*sin(1.0+tt*2.0)) ); /// punto cabeza



    float lcuerpo=dfLine(pa0, pa1, 0.05,  uv-vec2(-0.5,0.0));
    float lcuello=dfLine(pa5a, pa5, 0.05,  uv);
    float dcabeza= sdCircle(uv-pa5, 0.125 );
    float dPico= sdRhombus( uv-pa5-vec2(0.055,0.0), vec2(0.12) ) ;
    dcabeza=opS(dPico,dcabeza);
    dcabeza=opS(lcuello,dcabeza);


    //colita
    float  radio6=0.3*es;;
    vec2 pa6a=vec2(0.0,0.0);
    vec2 pa6b=pa6a+radio6*0.5*cossin(radians(150.0+10.0*sin(1.0+tt*2.0)) ); 
    float lcolita=dfLine(pa6a, pa6b, 0.05,  uv-vec2(-0.5,0.0));


    lcuerpo=lcuerpo/fwidth( lcuerpo);
    dcabeza=dcabeza/fwidth( dcabeza);
    lcuello=lcuello/fwidth( lcuello);
    lcolita=lcolita/fwidth( lcolita);

     //piena A
     col=pierna( uv,  col, tt);
     col=pierna( uv,  col, tt-2.0);

     col=pierna( uv-vec2(-0.8,0.0),  col,tt);
     col=pierna( uv-vec2(-0.8,0.0),  col,tt-2.0);
     //Pierna B



     col=mix(vec3(0.0,0.0,0.0) ,col, lcuerpo);
     col=mix(vec3(1.,0.0,0.0) ,col, dcabeza);
     col=mix(vec3(1.,0.0,0.0) ,col, lcuello);
     col=mix(vec3(1.,0.0,0.0) ,col, lcolita);
     col=mix(vec3(1.,0.0,0.0) ,col, dPico);

    fragColor = vec4(col,1.0);
}

