float time;

mat2 rot(float r){
  float s=sin(r),c=cos(r);
  return mat2(c,s,-s,c);
}

float rand(vec2 p){
  return fract(sin(dot(p,vec2(12.34,56.78)))*12345.678);
}

float box(vec3 p,vec3 s){
  p = abs(p)-s;
  return min(0.,max(p.x,max(p.y,p.z))) + length(max(p,0.));
}

float map(vec3 p){
  vec3 q = p;
  float t =floor(time)+pow(fract(time),.3);
  for(int i=0;i<5;i++){
    q = abs(q)-.6;
    q.xz *= rot(t);
    q.xy *= rot(.4+t*.6);
  }
  float d = box(q,vec3(.5,1.,.5));
  return d;
}

void cam(inout vec3 p){
  float t= time*.5;
  t = floor(time)+pow(fract(time),.3);
  p.xz *= rot(t);
  p.xy *= rot(.6);
}

vec3 normal(vec3 pos){
  vec2 k = vec2(1.,-1.);
  float e = 0.001;
  return normalize(
  k.xyy * (map(pos+k.xyy*e))+
  k.yxy * (map(pos+k.yxy*e))+
  k.yyx * (map(pos+k.yyx*e))+
  k.xxx * (map(pos+k.xxx*e))
  );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 p = vec2(gl_FragCoord.x / iResolution.x, gl_FragCoord.y / iResolution.y);
  vec2 uv = p;
  p -= .5;
  p /= vec2(iResolution.y/iResolution.x,1.);
  p = abs(p)-.5;
  p = abs(p)-.5;
  time = iTime/60.*132.;
  float beat = fract(time);
  float fft = texture(iChannel0,vec2(.5,.9)).x;
  vec3 ro=vec3(0.,0.,-12.),rd=normalize(vec3(-p,1.));
  cam(ro);
  cam(rd);
  vec3 col=vec3(0.),pos;
  float t = 0.01,d;
  for(int i=0;i<69;i++){
    pos =ro+rd*t;
    d = map(pos);
    if(d<0.01){
      vec3 n = normal(pos);
      vec3 ld = normalize(vec3(1.));
      float edge = clamp(pow(length(n-normal(pos-0.02*beat))*22.,22.),0.,1.);
      float spec = pow(clamp(dot(reflect(ld,n),rd),0.,1.),22.);
      col = vec3(.3,.6,.99)*edge + vec3(.3,.6,.99)*spec*2.;
      //break;
    }
    t+=.5;

  }
  float r0 = rand(uv+time)*2.-1.,r1 = rand(uv+time)*2.-1.,r2 = rand(uv+time)*2.-1.;
  vec3 tex = vec3(texture(iChannel1,uv-r0*.01).r,texture(iChannel1,uv+r1*0.001).g,texture(iChannel1,uv+r2*.001).b);

  col += mix(col,tex,pow(fft,.8)*1.6);
  fragColor = vec4(col,1);
}
