// A small test of dithering based on time
// Left side is using dithering
// Right side is not

#define sat(a) clamp(a, 0., 1.)

float time;

float _cir(vec2 p, float r)
{
    return length(p)-r;
}

vec3 rdr(vec2 uv)
{
    float t = time*10.;
    vec2 p = uv+vec2(sin(t*.25), cos(t))*.25;
    float c = _cir(p, .05);
    float shp = iResolution.x*.5;
    vec3 col = vec3(1.)*sat(c*shp);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    float t = iTime;

     // stepping the refresh rate to 20/s
    float stp = 1./20.;

    t = floor(t/stp)*stp;

    time = t;

    // dithering here thx https://www.shadertoy.com/view/tlVBWz
    if (uv.x < 0.)
        time = t-sat(texture(iChannel0, fragCoord/8.).x)*stp; 

    vec3 col = rdr(uv);

    fragColor = vec4(col,1.0);
}
