void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1 in the central square)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.;
    uv -= 1.;
    uv.x *= iResolution.x / iResolution.y;

    // Normalized mouse input (-1 to 1)
    vec2 mc;
    mc.x = iMouse.x;
    mc.y = iMouse.y;
    mc /= iResolution.xy;
    mc *= 2.;
    mc -= 1.;
    if (!(mc.x <= 1. || mc.x > -1.) || iMouse.xy == vec2(0., 0.)) {
      mc.x = 0.;
    }
    if (!(mc.y <= 1. || mc.y >= -1.) || iMouse.xy == vec2(0., 0.)) {
      mc.y = 0.;
    }

    // transformed pixel coordinates
    vec2 xy = uv;
    float gamma = 1./sqrt(1. - mc.x*mc.x);
    float t = xy.y;
    xy.y = gamma*(xy.y - mc.x*xy.x);
    xy.x = gamma*(xy.x - mc.x*t);

    // Time varying pixel color
    vec3 col = vec3(0., 0., 0.);

    // grid
    if (abs(mod(xy.x, 0.2)) < 0.005 || abs(mod(xy.y, 0.2)) < 0.005) {
        col = vec3(1., 1., 1.);
    }
    if (abs(xy.x) < 0.007 || abs(xy.y) < 0.007 || abs(xy.x - xy.y) < 0.007 || abs(xy.x + xy.y) < 0.007) {
        col = vec3(1., 1., 1.);
    }

    // A few world lines
    if (abs(xy.x - (+ 1.09 + .71*xy.y)) < 0.007) {
      col = vec3(0., 0., 1.);
    }
    if (abs(xy.x - (+ .76 - .22*xy.y)) < 0.007) {
      col = vec3(0., 1., 0.);
    }
    if (abs(xy.x - (- .18 + .88*xy.y)) < 0.007) {
      col = vec3(0., 1., 1.);
    }
    if (abs(xy.x - (- .51 - .32*xy.y)) < 0.007) {
      col = vec3(1., 0., 0.);
    }
    if (abs(xy.x - (- .31 - .62*xy.y)) < 0.007) {
      col = vec3(1., 0., 1.);
    }
    if (abs(xy.x - (- 1.35 + .30*xy.y)) < 0.007) {
      col = vec3(1., 1., 0.);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
