//IMG
void mainImage(out vec4 o, vec2 FC) {
    o = texelFetch(iChannel0, ivec2(FC), 0);
}

// A
#define rnd(x) fract(1e5 * sin(1e5 * x))
#define S smoothstep(0., .2, iTime)
#define K .062
#define F .06
#define Dab vec2(1,.4)*S
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

void mainImage(out vec4 o, vec2 FC) {
    vec2 uv = FC / iResolution.xy;
    vec2 v = texture(iChannel0, uv, .0).xy;
    vec2 d, delta;
    uv = .5 + (uv - .5) * rot(.02 * length(uv - .5));
    d = -v + texture(iChannel0,uv, 4.).xy;
    delta = Dab * d
        + vec2(-1., 1.) * v.x * v.y * v.y
        + vec2(F * (1. - v.x), -(K + F) * v.y);
    o = iTime < .1
        ? vec4(rnd(floor(uv.x * 10.)))
        : v.xyxx + delta.xyxx;
}
