#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Beam
    float x = iMouse.x / iResolution.x;
    if (x == 0.0) x += 0.5; // Have the beam start at the center
    x += sin (mod (iTime, 2.0 * PI) + uv.y) * 0.1;
    float radius = (cos (iTime * 3.0) * 0.05) + 0.2;
    vec3 mask1 = vec3 (smoothstep (x, x-radius, uv.x));
    vec3 mask2 = vec3 (smoothstep (x, x+radius, uv.x));
    vec3 line = mask1 + mask2;

    // Color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(line / col, 1.0);
}
