// variant of https://shadertoy.com/view/wtVBWK
//            simplied variant of my golfing of iapafoto's https://shadertoy.com/view/tlVBzV


void mainImage(out vec4 O, vec2 U) {
    float l = .2;
    vec3 R = iResolution,
         P = vec3(iTime,3,0)*7.,
         D = vec3( ( U - .5*R.xy ) / R.y + l*cos(.5*iTime) , 1 );
      // D = vec3( ( U - .5*R.xy ) / R.y                   , 1 ); // 1 tweet version
    for( D.yz *= mat2(4,-3,3,4)*l; 
         l > .1; 
         O = texture(iChannel0,ceil(P.xz)/40.),
         l = min( P.y - 8.* O.r, .11 )
       ) P += l*D;
    O *= length(step(.1,fract(P.xz))); // P alone would adds vertical ticks
 // O *= .2+P.y/P.z;                   // + shadowing and AO
}
