void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Output to screen
    fragColor = texture(iChannel0, uv);
}

float cellStep( in vec2 uv )
{
    float right1 = 1./iResolution.x;
    float up1 = 1./iResolution.y;

    float n1 = step(.5, texture(iChannel0, uv+vec2(-right1,up1)).x);
    float n2 = step(.5, texture(iChannel0, uv+vec2(0,up1)).x);
    float n3 = step(.5, texture(iChannel0, uv+vec2(right1,up1)).x);
    float n4 = step(.5, texture(iChannel0, uv+vec2(-right1, 0)).x);
    float n5 = step(.5, texture(iChannel0, uv+vec2(right1, 0)).x);
    float n6 = step(.5, texture(iChannel0, uv+vec2(-right1, -up1)).x);
    float n7 = step(.5, texture(iChannel0, uv+vec2(0, -up1)).x);
    float n8 = step(.5, texture(iChannel0, uv+vec2(right1, -up1)).x);
    float isAlive = step(.5, texture(iChannel0, uv).x);

    float neighbors = n1+n2+n3+n4+n5+n6+n7+n8;
    if (neighbors == 3.) {
    	return 1.;
    }
    if (neighbors == 2. && isAlive > 0.) {
    	return 1.;
    }   
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4  m  = iMouse / iResolution.xyxy;

    vec4 pic = sign(texture(iChannel1, uv+vec2(1./iResolution.x,0))-.5);

    float cell = cellStep(uv);
    cell = step(length(m.xy-uv),0.03)*step(0.,m.z) + cell;
    fragColor = vec4(cell,0,cell,1);
    //fragColor = (pic);
}
