void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord/iResolution.xy;

    //vec2 e = vec2(uv.x * fract(TIME+uv.x*7.0)*0.3,uv.y);

    float t = iTime;


 	vec2 uvdist = uv;
    uvdist.x *= sin(t*0.5);
    uvdist.y *= sin(t*0.5);  

    vec3 a = texture(iChannel0,mod(uvdist,1.0)).rgb;

    vec2 e = uv*10.0;    
    e.x *= a.g*0.5;



    // r == Values from the Waveform
    //vec3 sound = texture(iChannel2,e).r;

    vec3 b = texture(iChannel1,mod(e,1.0)).rgb;



	a = a * t*10.0;

    //vec3 d = vec3(uv.x,uv.y,0.0);
   	fragColor = vec4(b,1.0);
}
