void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; // Соединяем это в одну строку
    vec3 texColor = texture(iChannel0,xy).rgb; // Получаем от iChannel0 пиксель в координате xy
    vec3 toWB = vec3(0.2126, 0.7152, 0.0722);
    float depend = (sin(iTime*4.)+1.0)/2.0;
    vec3 WB = texColor * toWB;
    WB = vec3(WB.x + WB.y + WB.z);
    vec3 final = texColor * depend + WB * (1.- depend);
    fragColor = vec4(final, 1.); // Присваиваем экранному пикселю этот цвет
}
