void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{ 
    vec2 uv = fragCoord / iResolution.xy; 

    float x = fragCoord.x; 
    float y = fragCoord.y; 

    float r = sin(iTime); 
    float g = cos(iTime); 
    float b = 1.0 - sin(iTime); 

    float r1 = 1.0 - sin(iTime); 
    float g1 = sin(iTime); 
    float b1 = cos(iTime); 

    vec2 cc = vec2(400.0, 225.0); 
    float rad = 100.0; 
    if (pow((x-cc.x), 2.0) + pow((y-cc.y), 2.0) <= rad*rad) 
        fragColor = vec4(uv.x+ r,uv.y+ g, b, 1.0); 
    else 
        fragColor = vec4(uv.x+ r1,uv.y+ g1, b1, 1.0); 
}
