void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalization
    vec2 uv = fragCoord/iResolution.xy;

    // Circle
    vec2 nuv = fragCoord/iResolution.xy;
	nuv -= 0.5;
  	nuv /= vec2(iResolution.y / iResolution.x, 1);
    vec2 m;
  	m.x = atan(nuv.x / nuv.y) / 3.14;
  	m.y = 1. / length(nuv) * .3;
  	float d = m.y;


    // Loading texture
    vec4 op = texture(iChannel0, uv);



    // Shifting colors
    fragColor = op;
    fragColor.x = sin(d*op.x*(iTime*uv.x));
    fragColor.y = sin(d*op.y*(iTime*uv.y));


}
