// 266 chars
void mainImage(out vec4 F, vec2 U)
{
    U = (2.*U-(F.xy=iResolution.xy))/F.y;
    for(int i=0; i<8;i++)
    {
        U = abs(U)-.1;
        if(U.y>U.x)U=U.yx-.1;
        U *= mat2(cos(1.047-vec4(0,33,11,0)));
    }    
    F = vec4(step(.01,abs
                            (abs
                               (abs
                                   (length(U)-.13)
                               -.05)
                           -.02)

                 )
                 * (.8+.2*cos(6.*(2.5*length(U)+vec3(.0,.3,.6))))
              ,1.
             );
}
