#define TAU 6.2831853071

float circle(vec2 uv, vec2 pos, float radius){
    float ratio = iResolution.x/iResolution.y;
    return step(distance(vec2(uv.x*ratio, uv.y), vec2(pos.x+0.4, pos.y)), radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t = mod(iTime*0.7, 15.); // 
    float amt = min(sin(fract(t)*TAU/4.0)*1.5, 1.0) + floor(t) + 1.0;

    float sum = 0.0;

    float spacing = 0.2;

    float turn = TAU/amt;
    for(float i=0.; i<amt; i++){
        sum += circle(uv, vec2(0.5+cos(i*turn)*spacing, 0.5+sin(i*turn)*spacing), 0.3);
    }

    sum = 1.-mod(sum, 2.);
    vec3 col = vec3(sum);

    // Output to screen
    fragColor = vec4(col,1.0);
}
