// Fork of "Fake sphere from UVs" by Draedrus. https://shadertoy.com/view/wlKXWm
// 2020-02-28 15:25:26
// sphere mapping from https://www.shadertoy.com/view/wdSXRz

#define R iResolution
#define n(x) normalize(x)
#define Y(c) (c.x*.21+c.y*.71+c.z*.07)
#define r(x) n(x)*(asin(length(x))/1.57)
#define s(uv) texture(iChannel0, uv).rgb 
#define E vec2(5./R.x, 0.)
#define O(w, Z) max(w, Y(s(u+Z))-Y(N))
void mainImage( out vec4 c, in vec2 u )
{
    u = (-1.+2.*u/R.xy); u.x *= R.x/R.y;
    u = r(.5*u+.5*r(u));
    vec3 N = 2.*(-.5+n(s(u+vec2(iTime, 0.))));
    N = n(vec3(u, 1.)+N);
    u.x += iTime;
    c.a = max(0., dot(N, n(vec3(cos(iTime), sin(iTime), 0.))));
    N = s(u);
    c.rgb = (N*(.1+c.a))+c.a*c.a;
    c.rgb *= 1.-O(O(O(O(0., E.yx), (-E.yx)), E), (-E));
}
