// gigatron (c)  Motif for all texture ;

#define factor 20.0

const float amp  = 0.100 ;
const float freq = 16.0 ;
const float PI   = 3.1415926 ;

vec2 fx(vec2 uv) {

    float t = iTime*2.5;

     uv.x *=iResolution.x/iResolution.y;

     float d= 1.-smoothstep(0.3,0.35,length( uv-vec2(0.85,0.5)));

     float y = amp * sin(-PI * uv.y * freq    )+d; // -t
     float x = amp * cos(-PI * uv.x * freq -t )+d;



    uv.y = y ;
    uv.x = x ;

    float res =  length(max(abs(uv.y) , 1.0)+0.5)/
                 length(max(abs(y*uv.y+sin(t*0.02)) , 1.0)+1.0);

    return  2.*uv*res ;

}    


mat2 rot2d(vec2 uv, float angle) {


    float s = sin(angle) * 0.5+0.5;
    float c = cos(angle) * 0.5+0.5;

    mat2 rot = mat2(c,s,-s,c);

    return mat2(c,s,-s,c);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = uv ;

  //  uv = uv -0.5; 

   // uv *= rot2d(uv,iTime);

    uv = fx(uv);
    uv = fx(uv2); 

    vec3 tx = texture(iChannel0,uv).xyz;
    vec3 tx2 = texture(iChannel2,uv2 ).xxy;

    fragColor = vec4( 0.8*tx*sin(tx2*0.95)*2.0 ,1.0);
}
