void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 m = iMouse.xy/iResolution.xy + 0.25;
	vec2 uvMod = abs( floor(m*4.0)*0.25 - uv - 0.125);
    // Output to screen
    float tile = step(uvMod.x, 0.125) * step(uvMod.y, 0.125) ;
    //fragColor = vec4(length(mod(uv*8.0,1.0) -.5) ,0.0,0.0,1.0);
    fragColor = vec4(tile ,0.0,0.0,1.0);
}
