#define PI 3.14159265359
#define SQRT2 1.41421356237

#define MAX_FLT 3.402823e+38
#define EPSILON 1.192093e-07

#define sqrLength(x) dot((x),(x))

float clamp01(in float v) { return clamp(v, 0.0, 1.0); }
vec2 clamp01(in vec2 v) { return vec2(clamp01(v.x), clamp01(v.y)); }

//////////////
// Controls //
//////////////

// True iff using polar coordinates (otherwise cartesian) 
#define IS_USING_POLAR_COORD 1

// Focus point in [-1,1]x[-1,1] range
#define ZOOM_POINT vec2(-0.25, 0.5)

// Zoom convergence rate (positive number)
#define ZOOM_SPEED 1.0

// 1 for zooming-in, 0 for zooming-out
#define IS_ZOOM_IN 0

//////////////////////
// Texture Sampling //
//////////////////////

vec4 sample_clamp(in sampler2D tex, in vec2 uv) 
{
    return textureGrad(tex, clamp01(uv), dFdx(uv), dFdy(uv));
}

vec4 sample_repeat(in sampler2D tex, in vec2 uv) 
{
    return textureGrad(tex, fract(uv), dFdx(uv), dFdy(uv));
}

vec4 sample_border(in sampler2D tex, in vec2 uv) 
{
    bool isInBorder = ((0.0 <= uv.x) && (uv.x <= 1.0) && (0.0 <= uv.y) && (uv.y <= 1.0));
    if (isInBorder)
        return textureGrad(tex, fract(uv), dFdx(uv), dFdy(uv));
    return vec4(1.0, 0.0, 1.0, 1.0); // bg color - magenta    
}

////////////////////////
// Coordinate Systems //
////////////////////////

vec2 tex_to_cartesian(in vec2 uv, in vec2 origin)
{
    uv = 2.0 * uv - 1.0;
    uv -= origin;
    return uv;
}

vec2 cartesian_to_tex(in vec2 uv, in vec2 origin)
{
    uv += origin;
    uv = 0.5 * uv + 0.5;
    return uv;
}

vec2 tex_to_polar(in vec2 uv, in vec2 origin)
{
    uv = tex_to_cartesian(uv, origin);
    float r = length(uv); 
    float theta = atan(uv.y, uv.x);
    return vec2(r, theta);
}

vec2 polar_to_tex(in vec2 rt, in vec2 origin)
{
    float x = rt.x * cos(rt.y);
    float y = rt.x * sin(rt.y);  
    return cartesian_to_tex(vec2(x,y), origin);
}

////////////////
// Transforms //
////////////////

vec2 get_origin(in float t)
{
    return ZOOM_POINT;
}

vec2 transform(in vec2 uv, in float t)
{
#if IS_ZOOM_IN
    return vec2(exp(ZOOM_SPEED * -t), 1.0) * uv;
    //return vec2(1.0 / (1.0 + ZOOM_SPEED * t), 1.0) * uv;
#else
    return vec2(1.0 - exp(ZOOM_SPEED * -t), 1.0) * uv;
    //return vec2((ZOOM_SPEED * t) / (1.0 + ZOOM_SPEED * t), 1.0) * uv;
#endif
}

////////////////////
// Driver Program //
////////////////////

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 origin = get_origin(iTime);

    // uv_s: tex coord -> cartesian / polar coord   
    vec2 uv_s = fragCoord / iResolution.xy;
#if IS_USING_POLAR_COORD
    uv_s = tex_to_polar(uv_s, origin);
#else
    uv_s = tex_to_cartesian(uv_s, origin);
#endif

    // uv_t: transformation   
    vec2 uv_t = transform(uv_s, iTime);

    // uv_e: transformed cartesian / polar coord -> transformed tex coord
#if IS_USING_POLAR_COORD
	vec2 uv_e = polar_to_tex(uv_t, origin);
#else
    vec2 uv_e = cartesian_to_tex(uv_t, origin);
#endif    

    // final composition: texture using uv_e
    vec3 col = sample_border(iChannel0, uv_e).rgb;

    fragColor = vec4(col,1.0);
}
