float band(float t,float start,float end,float blur) 
{
    float step1 = smoothstep(start-blur, start+blur,t);
    float step2 = smoothstep(end+blur, end-blur,t);

    return step1*step2;
}
float rect(vec2 uv, vec2 p, float right,float left,float bottom,float top, float blur,float size)
{   
    uv -= p;     // transform
    uv /= size;  // scale

    float band1 = band(uv.x, left, right, blur);
    float band2 = band(uv.y, top, bottom, blur);

    return band1*band2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = fragCoord.xy / iResolution.xy ; // 0 <> 1

    uv -= .5; // -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(1);

    float mask = 0.;
    mask = rect(uv,vec2(0),0.1,-0.1,0.3,-0.3,.00001,1.); //(uv,p, right,left,bottom,top,blur,size)

    col = vec3(1,1,1)*mask; //rgb colour controll
    fragColor = vec4(col,1.);
}
