#define keyClick(a)   ( texelFetch(iChannel3,ivec2(a,0),0).x > 0.)

void mainImage( out vec4 O, vec2 u )
{
    vec2 U,P; vec3 D; int s,t;

    if ( keyClick(32) || iMouse.xy==vec2(0) ) {               // display full storage
        U = vec2(3,2)*u/R;
        s = int(U.x)%3 + 3*int(U.y);
        P = 2.* fract(U) - 1.;
    } else {                                                  // mouse browse stored images 
        t = int( iMouse.x / R.x * float(n*n*6) );
        s = t/(n*n);
        P = ( u/R + vec2( t%n ,  t%(n*n) / n ) ) / float(n) *2. - 1.;
    }

    D =   s==0 ? vec3(1,-P.y,-P.x) : s==3 ? vec3(-1,-P.y,P.x) // fetch image
        : s==1 ? vec3(P.x,1,P.y)   : s==4 ? vec3(P.x,-1,-P.y)
        : s==2 ? vec3(P.x,-P.y,1)  :        vec3(-P.x,-P.y,-1);
    O = texture(iChannel0, D );

    if (u.y < 5.) O = vec4( int(u.x/R.x* float(n*n*6)) < iFrame/2, 0,0,0 ); // loading bar
}

void mainCubemap( out vec4 O, vec2 U, vec3 C, vec3 D )
{
    vec3 A = abs(D); 
    int t = A.x > A.y ? A.x > A.z ? 0 : 2 : A.y > A.z ? 1 : 2; if (D[t]<0.) t += 3;
    t =  t * n*n + int(U.y)/W  * n +  int(U.x)/W;
    O = iFrame/2 == t 
            ? texture(iChannel1, fract( U/R *float(n)) )
            : texture(iChannel0, D );
}
