// I also posted a version here: http://glslsandbox.com/e#71503.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
    for(float i=1.; i<9.; i++){
        uv.x += .5/i*sin(1.9 * i * uv.y + iTime / 2. - cos(iTime / 66. + uv.x))+21.;
        uv.y += .4/i*cos(1.6 * i * uv.x + iTime / 3. + sin(iTime / 55. + uv.y))+31.;
    }
    fragColor = vec4(sin(3. * uv.x - uv.y), sin(3. * uv.y), sin(3. * uv.x), 1);
}
